var swiper=new Swiper("#gallery",{direction:"horizontal",loop:!0,centeredSlides:!0,slidesPerView:3,spaceBetween:0,grabCursor:!0,speed:500,keyboard:{enabled:!0,onlyInViewport:!1},navigation:{nextEl:".swiper-navigation__next",prevEl:".swiper-navigation__prev"}}).on("setTransition",(()=>{document.querySelector("#gallery > .swiper-wrapper").classList.add("swiper-move")})).on("slideNextTransitionStart",(()=>{document.querySelector("#gallery > .swiper-wrapper").classList.add("swiper-move")})).on("transitionEnd",(()=>{document.querySelector("#gallery > .swiper-wrapper").classList.remove("swiper-move")}));